#include <sys/types.h>
#include <sys/stat.h>
#include <fcntl.h>
#include <unistd.h>
#include <stdio.h>

void copy_file(int in_fd, int out_fd) {
    char buffer[4096];
    size_t read_bytes;
    while ((read_bytes = read(in_fd, buffer, 4096)) > 0) {
        write(out_fd, buffer, read_bytes);
    }
}

int main(int argc, char *argv[]) {
    int in_fd = open("file1", O_RDONLY, S_IRUSR | S_IWUSR);
    int out_fd = open("file2", O_WRONLY | O_CREAT | O_TRUNC, S_IRUSR | S_IWUSR);
    copy_file(in_fd, out_fd);
    close(in_fd);
    close(out_fd);
    return 0;
}
